export function OrbitingCirclesDemo() {
  return (
    <div className="relative flex h-[500px] w-full flex-col items-center justify-center overflow-hidden rounded-lg bg-transparent">
      <span className="pointer-events-none flex flex-row items-center whitespace-pre-wrap bg-gradient-to-b from-pink-500 to-purple-300 bg-clip-text text-center text-8xl font-semibold leading-none text-transparent dark:from-purple-300 dark:to-pink-500">
        InstaTryOn <MousePointerClick className=" text-pink-500 dark:text-pink-300 w-20 h-20 pointer-events-none flex flex-row items-center whitespace-pre-wrap bg-gradient-to-b from-pink-500 to-gray-300 bg-clip-text text-center text-8xl font-semibold leading-none text-transparent dark:from- dark:to-pink-500"/>
      </span>

      {/* Inner Circles */}
      <OrbitingCircles
        className="size-[30px] border-none bg-transparent"
        duration={20}
        delay={20}
        radius={80}
      >
        <FaShopify className="w-24 h-24"/>
      </OrbitingCircles>
      <OrbitingCircles
        className="size-[30px] border-none bg-transparent"
        duration={20}
        delay={10}
        radius={80}
      >
        <FaWordpress className="w-16 h-16"/>
      </OrbitingCircles>

      {/* Outer Circles (reverse) */}
      <OrbitingCircles
        className="size-[50px] border-none bg-transparent"
        radius={130}
        duration={20}
        reverse
      >
        <FaNpm className="w-16 h-16"/>
      </OrbitingCircles>
      <OrbitingCircles
        className="size-[100px] border-none bg-transparent grayscale"
        radius={190}
        duration={20}
        delay={20}
        reverse
      >
        <Icons.woo/>
      </OrbitingCircles>
    </div>
  );
}

